import React, { Component } from "react";
import Cookies from 'universal-cookie';
import "bootstrap/dist/css/bootstrap.min.css";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

import {
    Table,
    Button,
    Container,
    Modal,
    ModalHeader,
    ModalBody,
    FormGroup,
    ModalFooter
} from "reactstrap";


const cookies = new Cookies();

class Reclutador extends Component{
    constructor() {
        super();
        if(cookies.get('id') == undefined) window.location.href = "./login"
        this.state = {
            id: cookies.get('id')
        };
        this.handleChange = this.handleChange.bind(this);
    }


    render(){
        return <div>
           <h1>Hola {this.state.id}</h1>
        </div>
    }

}

function Menu(props){
    return ( <div>
        <nav role="navigation">
        <div id="menuToggle">
        <input type="checkbox"/>
        <span></span>
        <span></span>
        <span></span>
        <ul id="menu">
        <button onClick={()=>props.this.mostrarModalBuscar()}><li>Buscar Aplicante</li></button>
        <div className='boxer'/>
        <button onClick={()=>props.this.cerrarSesion()}><li>Cerrar Sesion</li></button>
        </ul>
        </div>
        </nav>
        <div className='xml'>
        <h2>Aplicantes en el departamento</h2>
        </div>
        </div>
    );
}

function Search(props) {
    var [startDateF, setStartDateF] = useState(null);
    return(
        <Modal isOpen={props.this.state.modalBuscar} fade={false}>
            <ModalHeader>
            <div><h3>Buscar Aplicante</h3></div>
            </ModalHeader>
            <ModalBody>
                <FormGroup>
                <label>
                    Nombre: 
                </label>
                <input
                    className="form-control"
                    name="user"
                    type="text"
                    onChange={props.this.handleChangeS}
                />
                </FormGroup>
                <FormGroup>
                <label>
                    Puesto
                </label>
                <Dropdown name = "estado" options={props.this.state.estado} onChange={props.this.handleChangeES} value={props.this.state.search.estado} placeholder="--" />
                </FormGroup>


                <FormGroup>
                <label>
                    Fecha Postulacion: 
                </label>
                <DatePicker
                    name = "fin"
                    dateFormat="yyyy-MM-dd"
                    selected={startDateF}
                    onChange={(date) => setStartDateF(date)}
                    fixedHeight
                    //withPortal
                />
                </FormGroup>

            </ModalBody>
            <ModalFooter>
                <Button
                color="primary"
                onClick={() => props.this.buscar(startDate, startDateF)}
                >
                Buscar
                </Button>
                <Button
                color="danger"
                onClick={() => props.this.cerrarModalBuscar()}
                >
                Cancelar
                </Button>
            </ModalFooter>
        </Modal>   
    );
}

function Fethc(props) {
    return(
        <Container>
        <br />
        {(() => {
                if(props.this.state.bus === true){
                    return<Container>
                    <Button style={{float: 'right'}}
                    className="btn btn-danger"
                    onClick={() => this.cerrarBusqueda()}
                    >
                    X
                    </Button>
                    <div class = "box"></div>
                    </Container>

                }
        })()}
        <Table>
            <thead>
            <tr>
                <th>Nombre</th>
                <th>Puesto</th>
                <th>Fecha Postulacion</th>
                <th>Datos Personales</th>
                <th>CV</th>
            </tr>
            </thead>

            {props.this.state.tasks.map((dato) => (
                (() => {
                    if(dato.estado == 1){
                        return <Ifyes dato = {dato} this= {this}/>
                    }else{
                        return <Elsen dato = {dato} this = {this}/>
                    }
                })()
            ))}

        </Table>
        </Container>
    );
}

function Ifyes(props) {
    var dato = props.dato;
    return(
        <tbody style={{backgroundColor : "#81C784"}}>
        <tr key={dato.id} >
        <td>{dato.user}</td>
        <td>{dato.pass}</td>
        <td>
            <Button
            onClick={() => props.this.mostrarModalActualizar(dato)}
            >
            Ver
            </Button>
        </td>    
        <td>
            <Button
            onClick={() => props.this.mostrarModalActualizar(dato)}
            >
            Descargar
            </Button>
        </td>
        <td>
            <Button
            color="Success"
            onClick={() => props.this.mostrarModalActualizar(dato)}
            >
            Aceptar
            </Button>
            <Button color="danger" onClick={()=> props.this.eliminar(dato)}>Rechazar</Button>
        </td>
        </tr>
        </tbody>
    );    
}

function Elsen(props) {
    var dato = props.dato;
    return(
        <tbody style={{backgroundColor : "#F44336"}}>
        <tr key={dato.id}>
        <td>{dato.user}</td>
        <td>{dato.pass}</td>
        </tr>
        </tbody>
    );
}

export default Reclutador;
