import React, { Component } from "react";

import {
    Table,
    Button,
    Container,
    Modal,
    ModalHeader,
    ModalBody,
    FormGroup,
    ModalFooter,
} from "reactstrap";

class AdminUser extends Component{

    mostrarModalInsertar(){

    }
    cerrarModalInsertar(){

    }
    mostrarModalActualizar(dato){

    }
    cerrarModalActualizar(){

    }

    editar(dato){

    }
    eliminar(dato){

    }
    insertar(){

    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
          [name]: value
        });
    }

    constructor() {
        super();
        this.state = {
            modalActualizar: false,
            modalInsertar: false,
            tasks: [],
            form :{
                id: '',
                user: '',
                pass: '',
                estado: '',
                inicio: '',
                fin: '',
                rol: '',
                depa: ''
            },
            rols: [], 
            depas: []
        };
    }

    render (){
        return<div>
            <Able/>
            <ModalInsertar />
            <ModalActualizar />
        </div>
    }
}

function Able() {    
    return(
        <div className="crud">
            <Container>
            <br />
            <Button color="success" onClick={()=>this.mostrarModalInsertar()}>Crear</Button>
            <br />
            <br />
            <Table>
                <thead>
                <tr>
                    <th>Usuario</th>
                    <th>Contrase√±a</th>
                    <th>Fecha Inicio</th>
                    <th>Fecha Fin</th>
                    <th>Rol</th>
                    <th>Departamento</th>
                </tr>
                </thead>
                <tbody>
                {this.state.data.map((dato) => (
                    <tr key={dato.id}>
                    <td>{dato.id}</td>
                    <td>{dato.personaje}</td>
                    <td>{dato.anime}</td>
                    <td>
                        <Button
                        color="primary"
                        onClick={() => this.mostrarModalActualizar(dato)}
                        >
                        Editar
                        </Button>{" "}
                        <Button color="danger" onClick={()=> this.eliminar(dato)}>Eliminar</Button>
                    </td>
                    </tr>
                ))}
                </tbody>
            </Table>
            </Container>
            </div>
    );
}

function ModalInsertar() {
    return (
        <Modal isOpen={this.state.modalInsertar}>
        <ModalHeader>
        <div><h3>Insertar Personaje</h3></div>
        </ModalHeader>
        <ModalBody>
            <FormGroup>
            <label>
                Personaje: 
            </label>
            <input
                className="form-control"
                name="personaje"
                type="text"
                onChange={this.handleChange}
            />
            </FormGroup>
            <FormGroup>
            <label>
                Anime: 
            </label>
            <input
                className="form-control"
                name="anime"
                type="text"
                onChange={this.handleChange}
            />
            </FormGroup>
        </ModalBody>
        <ModalFooter>
            <Button
            color="primary"
            onClick={() => this.insertar()}
            >
            Insertar
            </Button>
            <Button
            className="btn btn-danger"
            onClick={() => this.cerrarModalInsertar()}
            >
            Cancelar
            </Button>
        </ModalFooter>
        </Modal>
    );

}

function ModalActualizar() {
    return(
        <Modal isOpen={this.state.modalActualizar}>
        <ModalHeader>
        <div><h3>Editar Registro</h3></div>
        </ModalHeader>
        <ModalBody>
            <FormGroup>
            <label>
                Personaje: 
            </label>
            <input
                className="form-control"
                name="personaje"
                type="text"
                onChange={this.handleChange}
                value={this.state.form.personaje}
            />
            </FormGroup>

            <FormGroup>
            <label>
                Anime: 
            </label>
            <input
                className="form-control"
                name="anime"
                type="text"
                onChange={this.handleChange}
                value={this.state.form.anime}
            />
            </FormGroup>
        </ModalBody>
        <ModalFooter>
            <Button
            color="primary"
            onClick={() => this.editar(this.state.form)}
            >
            Editar
            </Button>
            <Button
            color="danger"
            onClick={() => this.cerrarModalActualizar()}
            >
            Cancelar
            </Button>
        </ModalFooter>
        </Modal>
    );

}

export default AdminUser;
