import React, { Component} from "react";
import Swal from 'sweetalert2'
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import '../styles/asys.css'
import {
    Container,
    Modal,
    Table
} from "reactstrap";

class AdminSystem extends Component{
    constructor() {
        super();
        this.state = {
            load2: false,
            value: '',
            tasks1: [],
            tasks2: [],
            tasks3: [],
            tasks4: [],
            tasks5: [],
            tasks6: []
        };
        this.handleChange = this.handleChange.bind(this);
        this.cargarXml = this.cargarXml.bind(this);
        this.sendXml = this.sendXml.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
          [name]: value
        });
    }
    sendXml(){
        this.setState({load2: true})
        fetch('/carga', {
            method: 'POST',
            body: JSON.stringify({"xml": this.state.value}),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => { 
                Swal.fire({
                    title: data.msg,
                    showClass: {
                      popup: 'animate__animated animate__fadeInDown'
                    },
                    hideClass: {
                      popup: 'animate__animated animate__fadeOutUp'
                    }
                  })
                this.setState({load2: false})
            })
            .catch(err => console.error(err));
            this.setState({value: ''});

    }

    cerrarSesion=()=>{
        window.location.href='./';
    }

    cargarXml(e){
        const file = e.target.files[0];
        if ( !file ) return;

        const fileReader = new FileReader();

        fileReader.readAsText( file );

        fileReader.onload = () => {
        console.log( fileReader.result );
        this.setState({value: fileReader.result});
        }

        fileReader.onerror = () => {
        console.log( fileReader.error );
        }
    }

    render (){
        return <div>
            <nav role="navigation">
            <div id="menuToggle">
                <input type="checkbox"/>
                <span></span>
                <span></span>
                <span></span>
                <ul id="menu">
                <button onClick={()=>this.cerrarSesion()}><li>Cerrar Sesion</li></button>
                </ul>
            </div>
            </nav>
            <div className="xml">
                <h1>Admin Sistema</h1>
            </div>
            <div className="box"></div>
            <Container>
                <Tabs >
                    <TabList>
                        <Tab>Carga</Tab>
                        <Tab>Reporte 1</Tab>
                        <Tab>Reporte 2</Tab>
                        <Tab>Reporte 3</Tab>
                        <Tab>Reporte 4</Tab>
                        <Tab>Reporte 5</Tab>
                        <Tab>Reporte 6</Tab>
                    </TabList>
                    <TabPanel >
                        <Report_1 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_2 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_3 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_4 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_5 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_6 this = {this} />
                    </TabPanel>
                </Tabs>
            </Container>


            <Load this= {this}/>
        </div>
    }
}


function Tab1(props){
    return(
        <div className="xml">
                <h3>Carga Masiva</h3>
                <div>
                    <input type="file" 
                        multiple={false}
                        accept=".xml"
                        onChange={props.this.cargarXml}
                    />
                    <p>
                    <textarea name="value" value={props.this.state.value} onChange={props.this.handleChange} id="textExample" rows="10"  cols="60"></textarea>
                    </p>
                    <p>
                        <button onClick={props.this.sendXml} id='type'>Confirmar</button>
                    </p>
                </div>
        </div>
    );
}

function Report_1(props) {
    return(
        <Container>
        <div className="xmt">
            <h2>Reporte 1 </h2>
            </div>
            <div className="xml">
            <text>Organigrama de la institución, se puede seleccionar tanto la institución completa o un departamento en específico</text>
        </div>    
        <div className="box"></div>
        <Table>
            <thead>
            <tr>
                <th>Requisito</th>
                <th>Estado</th>
                <th>Ver</th>
            </tr>
            </thead>
            {props.this.state.tasks1.map((dato) => (
                <tbody>
                <tr>
                <td>{dato.nombre}</td>
                <td>{dato.puesto}</td> 
                </tr>
                </tbody>
            ))}
        </Table>
        </Container>
    );
}

function Report_2(props) {
    return(
        <h4>TAB 3</h4>
    );
}


function Report_3(props) {
    return(
        <h4>TAB 4</h4>
    );
}

function Report_4(props) {
    return(
        <h4>TAB 5</h4>
    );
}

function Report_5(props) {
    return(
        <h4>TAB 6</h4>
    );
}

function Report_6(props) {
    return(
        <h4>TAB 7</h4>
    );
}


function Load(props){
    return (
        <Modal isOpen={props.this.state.load2} fade={false}>
        <div class="load">
        <hr/><hr/><hr/><hr/>
        </div>
        </Modal>
    );
}


export default AdminSystem;
