import React, { Component, useState } from "react";
import Cookies from 'universal-cookie';
import "bootstrap/dist/css/bootstrap.min.css";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import axios from 'axios';


import {
    Table,
    Button,
    Container,
    Modal,
    ModalHeader,
    ModalBody,
    FormGroup,
    ModalFooter
} from "reactstrap";

const cookies = new Cookies();

class Applicant extends Component{

    constructor(){
        super();
        if(cookies.get('id') === undefined) window.location.href = "./login"
        this.state = {
            id: cookies.get('id'),
            tasks: [],
            nombre: '',
            Requisitos: [],
            load2: true,
            modalVer: false,
            modalCargar: false,
            modalCorregir: false,
            modalVer2:true,
            load: true,
            data: {},
            profile:{
                nombre: '',
                fecha: '',
                apellido: '',
                correo: '', 
                direccion: '',
                telefono: '',
                cv: '',
                primera: '',
                depa: '',
                puestoID: '',
                puesto: ''
            },
            nuevoCV: null,
            arreglo: ''
        }
        this.handleChange = this.handleChange.bind(this);
        //this.handleChangeF = this.handleChangeF.bind(this);
        this.handleChangeC = this.handleChangeC.bind(this);
        this.fetchProfile();
    }

    cerrarSesion(){
        cookies.remove('id', {path: "/"});
        window.location.href='./';
    }

    fetchProfile(){
        fetch('/selectPr', {
            method: 'POST',
            body: JSON.stringify({
                cui: this.state.id
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({profile: data});
                this.fetchRequisitos(data.puestoID);
            })
            .catch(err => console.error(err));
    }

    fetchProfile2(){
        fetch('/selectPr', {
            method: 'POST',
            body: JSON.stringify({
                cui: this.state.id
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({profile: data, load2:false});
            })
            .catch(err => console.error(err));
    }

    Comprobar(){
        console.log('KHE')
        if(this.state.profile.primera == 1){
            this.setState({modalCargar: true, load2: false});
        }else{
            this.setState({load2:false});
            this.Archivos();
        }
    }

    fetchRequisitos(id){
        fetch('/requisitos', {
            method: 'POST',
            body: JSON.stringify({
                puesto: id
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                console.log(data)
