    hacerArreglo(formatos){
        var aux = ""
        formatos.map((requi, index) =>
            {

                if(index != 0){
                    aux += ","
                }
                aux += "."
                aux += requi.formato
            })
        console.log(aux)
        return aux;
    }
    DescargarCV(cv){
        this.setState({load2: true})
        fetch('/controller', {
            method: 'POST',
            body: JSON.stringify({
                d:cv
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.blob())
            .then((blob) => {
                // Create blob link to download
                const url = window.URL.createObjectURL(
                  new Blob([blob]),
                );
                const link = document.createElement('a');
                link.href = url;
                link.setAttribute(
                  'download',
                  `${cv}`,
                );
                // Append to html link element page
                document.body.appendChild(link);
                // Start download
                link.click();
                this.setState({load2: false})
                // Clean up and remove the link
                link.parentNode.removeChild(link);
            });
    }

    handleChangeC(e){
        const formData = new FormData();
        formData.append(
            "file",
            e.target.files[0]
        );
        this.setState({load2: true})
        axios.post("/upload",formData, {})
        .then(res => {
            this.setState({nuevoCV: res.data.msg, load2: false})
        })
    }
    primera(){
        fetch('/primera', {
            method: 'PUT',
            body: JSON.stringify({
                cui: this.state.id
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.Archivos()
            })
            .catch(err => console.error(err));
        this.Archivos()
    }

    Cargar(){
        this.setState({load2: true})
        console.log(this.state.Requisitos);
        for (const requi of this.state.Requisitos){
            if(requi.obligatorio == 1 && requi.util == null){
                window.alert("Requisitos obligatorios SIN CARGAR");
                this.setState({load2: false})
                return;
            }
        }
        for (const requi of this.state.Requisitos){
            if(requi.util == null) continue;
            fetch('/archivo', {
                method: 'POST',
                body: JSON.stringify({
                    ruta: requi.util,
                    cui: this.state.id,
                    prid: requi.id
                }),
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
                })
                .then(res => res.json())
                .then(data => {
                    console.log(data)
                })
                .catch(err => console.error(err));
        }
        window.alert("Expediente enviado para revisar correctamente")
        this.primera();
        this.setState({modalCargar: false, load2:false})
    }

    handleChangeF(e, requi) {
        console.log(e, requi)
        this.setState({load2: true})
        console.log(e.size)
        if(e.size > requi.tamanio * 1048576){
            window.alert(`El archivo supera el limite del tamaÃ±o permitido (${requi.tamanio})MB`);
            this.setState({load2: false})
            return;
        }
        const formData = new FormData();
        formData.append(
            "file",
            e
        );
        axios.post("/uploadExp",formData, {})
        .then(res => {
            requi.util = res.data.msg
            this.setState({load2: false})
        })
    }

    Archivos(){
        fetch('/selectExp', {
            method: 'POST',
            body: JSON.stringify({
                cui: this.state.id
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({tasks: data, load: false});
            })
            .catch(err => console.error(err));
    }

