import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

import {
    Table,
    Button,
    Container,
    Modal,
    ModalHeader,
    ModalBody,
    FormGroup,
    ModalFooter
} from "reactstrap";

class AdminUser extends Component{
    constructor() {
        super();
        this.state = {
            modalActualizar: false,
            modalInsertar: false,
            tasks: [],
            data :{
                id: '',
                user: '',
                pass: '',
                estado: '',
                inicio: '',
                fin: '',
                rol: '',
                depa: ''
            },
            rols: ["Coordinador", "Reclutador", "Revisor"], 
            depas: []
        };
        this.handleChange = this.handleChange.bind(this);

        //this.fetchTasks();
        this.fetchdepa();
    }
    fetchTasks() {
        fetch('/selectU')
          .then(res => res.json())
          .then(data => {
            this.setState({tasks: data});
          });
    }
    fetchdepa() {
        fetch('/depas')
          .then(res => res.json())
          .then(data => {
            console.log(data)
            const aux = [];
            for(const i of data){
                console.log(i)
                aux.push(i[0])    
            }
            this.setState({depas: aux});
            console.log(this.state.depas)
        });
    }


    mostrarModalInsertar(){
        this.setState({modalInsertar: true})
        console.log(this.state.modalInsertar)
    }
    cerrarModalInsertar(){
        this.setState({modalInsertar: false})
    }
    mostrarModalActualizar(dato){

    }
    cerrarModalActualizar(){

    }

    editar(dato){

    }
    eliminar(dato){

    }
    insertar(){

    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
          data:{
              ...this.state.data,
              [name]: value
          },
        });
    }

    render (){
        return(
            <div className="crud">
            <Container>
            <br />
            <Button color="success" onClick={()=>this.mostrarModalInsertar()}>Crear Usuario</Button>
            <br />
            <br />
            <Table>
                <thead>
                <tr>
                    <th>Usuario</th>
                    <th>Contraseña</th>
                    <th>Fecha Inicio</th>
                    <th>Fecha Fin</th>
                    <th>Rol</th>
                    <th>Departamento</th>
                </tr>
                </thead>
                <tbody>
                {this.state.tasks.map((dato) => (
                    <tr key={dato.id}>
                    <td>{dato.user}</td>
                    <td>{dato.pass}</td>
                    <td>{dato.inicio}</td>
                    <td>{dato.fin}</td>
                    <td>{dato.rol}</td>
                    <td>{dato.depa}</td>
                    <td>
                        <Button
                        color="primary"
                        onClick={() => this.mostrarModalActualizar(dato)}
                        >
                        Editar
                        </Button>{" "}
                        <Button color="danger" onClick={()=> this.eliminar(dato)}>Eliminar</Button>
                    </td>
                    </tr>
                ))}
                </tbody>
            </Table>
            </Container>

            <Modal isOpen={this.state.modalInsertar} fade={false}>
            <ModalHeader>
            <div><h3>Insertar Usuario</h3></div>
            </ModalHeader>
            <ModalBody>
                <FormGroup>
                <label>
                    Usuario: 
                </label>
                <input
                    className="form-control"
                    name="user"
                    type="text"
                    onChange={this.handleChange}
                />
                </FormGroup>
                <FormGroup>
                <label>
                    password: 
                </label>
                <input
                    className="form-control"
                    name="pass"
                    type="text"
                    onChange={this.handleChange}
                />
                </FormGroup>
                <FormGroup>
                <label>
                    Rol
                </label>
                <Dropdown name = "rol" options={this.state.rols} onChange={this.handleChange} value={this.state.data.rol} placeholder="Selecciona Rol" />
                </FormGroup>
                <FormGroup>
                <label>
                    Departamento
                </label>
                <Dropdown name = "depa" options={this.state.depas} onChange={this.handleChange} value={this.state.data.depa} placeholder="Selecciona Departamento" />
                </FormGroup>
            </ModalBody>
            <ModalFooter>
                <Button
                color="primary"
                onClick={() => this.insertar()}
                >
                Insertar
                </Button>
                <Button
                className="btn btn-danger"
                onClick={() => this.cerrarModalInsertar()}
                >
                Cancelar
                </Button>
            </ModalFooter>
            </Modal>

            <Modal isOpen={this.state.modalActualizar} fade={false}>
            <ModalHeader>
            <div><h3>Editar Usuario</h3></div>
            </ModalHeader>
            <ModalBody>
                <FormGroup>
                <label>
                    Usuario: 
                </label>
                <input
                    className="form-control"
                    name="user"
                    type="text"
                    onChange={this.handleChange}
                    value={this.state.data.user}
                />
                </FormGroup>

                <FormGroup>
                <label>
                    Contraseña: 
                </label>
                <input
                    className="form-control"
                    name="pass"
                    type="text"
                    onChange={this.handleChange}
                    value={this.state.data.pass}
                />
                </FormGroup>
                <FormGroup>
                <label>
                    Rol
                </label>
                <Dropdown name = "rol" options={this.state.rols} onChange={this.handleChange} value={this.state.data.rol} placeholder="Selecciona Rol" />
                </FormGroup>
                <FormGroup>
                <label>
                    Departamento
                </label>
                <Dropdown name = "depa" options={this.state.depas} onChange={this.handleChange} value={this.state.data.depa} placeholder="Selecciona Departamento" />
                </FormGroup>
            </ModalBody>
            <ModalFooter>
                <Button
                color="primary"
                onClick={() => this.editar(this.state.data)}
                >
                Editar
                </Button>
                <Button
                color="danger"
                onClick={() => this.cerrarModalActualizar()}
                >
                Cancelar
                </Button>
            </ModalFooter>
            </Modal>           
        </div>
        );
    }
}




export default AdminUser;
