import React, { Component } from 'react';
import '../styles/guest.css';
import "bootstrap/dist/css/bootstrap.min.css";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

import {
    Table,
    Button,
    Container,
    Modal,
    ModalHeader,
    ModalBody,
    FormGroup,
    ModalFooter
} from "reactstrap";
class guest extends Component{

    constructor() {
        super();
        this.state = {
            tasks: [],
            modalBuscar: false,
            modalSeleccionar: false,
            date : getCurrentDate()
        };

        this.fetchTasks()
    }
    fetchTasks() {
        fetch('/guest')
          .then(res => res.json())
          .then(data => {
            this.setState({tasks: data});
          });
    }

    buscar(){

    }

    mostrarModalBuscar(){
        this.setState({modalBuscar: true})
    }
    cerrarModalBuscar(){
        this.setState({modalBuscar: false})
        //this.fetchTasks();
    }

    render(){
        return <div> 
        <Main/>
        <div class = "box"></div>
        <Search this = {this}/>    
        <Carrousel tasks = {this.state.tasks} />
        </div>
    }
}

function Main(){
    return(
        <header>
        <div class="padre-sup">
            <div class="hijo-sup">
                <div class="logo-titulo">		
                    <h1 id="titulo-h"><a class="hipervinculo-h" href="/">TOTONET S.A</a></h1>
                </div>
                <nav id="menu-header">
                    <ul id="lista">
                        <a class="item hipervinculo-h" href="/login"><li> INICIAR SESION</li></a>
                    </ul>
                </nav>
            </div>
        </div>
        <div id="franja-amarilla-h"></div>  
    </header> 
    );
}

function Ifblock(props){
    return (
        <div class="card text-center" style={{backgroundImage: `url(${props.e.Imagen})`, backgroundSize: 'cover'}}>
        <div class="title">
        <h2>{props.e.Puesto}</h2>
        </div>
        <div class="option">
            <ul>
            <li> <i class="fa fa-check" aria-hidden="true"></i>Salario: {props.e.Salario} </li>
            <li> <i class="fa fa-check" aria-hidden="true"></i>Nombre Departamento: {props.e.Departamento} </li>                    
            <li> <i class="fa fa-check" aria-hidden="true"></i>Categorias: </li>                    
            <ul>
                {
                    props.e.Categorias.map(dsa =>
                        <li> <i class="fa fa-times" aria-hidden="true"></i>{dsa.nombre} </li>

                    )
                }
            </ul>
            </ul>
        </div>
        <a href= "/form">Seleccionar </a>
        </div>
    );
}

function Elseblock(props){
    return (
        <div class="card text-center">
        <div class="title">
        <h2>{props.e.Puesto}</h2>
        </div>
        <div class="option">
            <ul>
            <li> <i class="fa fa-check" aria-hidden="true"></i>Salario: {props.e.Salario} </li>
            <li> <i class="fa fa-check" aria-hidden="true"></i>Nombre Departamento: {props.e.Departamento} </li>                    
            <li> <i class="fa fa-check" aria-hidden="true"></i>Categorias: </li>                    
            <ul>
                {
                    props.e.Categorias.map(dsa =>
                        <li> <i class="fa fa-times" aria-hidden="true"></i>{dsa.nombre} </li>

                    )
                }
            </ul>
            </ul>
        </div>
        <a href= "/form">Seleccionar </a>
        </div>
    );
}

function Carrousel(props){
    return(
        <body>
            <Button color="primary" onClick={()=>props.this.mostrarModalBuscar}>Buscar Puesto</Button>
            <section>
                <div class="container-fluid">
                <div class="container">
                <div class="row">
                {               
                    props.tasks.map((e, index) => 
                        <div>
                        {
                            <div class="col-sm-4">
                            {(() => {
                                console.log(index);
                                if(e.Imagen != null){
                                    return <Ifblock e = {e}/>
                                }else{
                                    return <Elseblock e = {e}/>
                                }
                            })()}
                             <div class = "box"></div>
                            </div>                
                        } 
                        </div>
                    )   
                }
                </div>
                </div>
                </div>   
            </section> 
        </body> 
    );

}


function Search(props){
    return(
        <Modal isOpen={props.this.state.modalBuscar} fade={false}>
            <ModalHeader>
            <div><h3>Buscar</h3></div>
            </ModalHeader>
            <ModalBody>
                <FormGroup>
                <label>
                    Usuario: 
                </label>
                <input
                    className="form-control"
                    name="user"
                    type="text"
                    onChange={props.this.handleChange}
                />
                </FormGroup>
                <FormGroup>
                <label>
                    password: 
                </label>
                <input
                    className="form-control"
                    name="pass"
                    type="text"
                    onChange={props.this.handleChange}
                />
                </FormGroup>
                <FormGroup>
                <label>
                    Categoria
                </label>
                <Dropdown name = "rol" placeholder="Selecciona Categoria" />
                </FormGroup>
                <FormGroup>
                <label>
                    Departamento
                </label>
                <Dropdown name = "depa"  placeholder="Selecciona Departamento" />
                </FormGroup>
            </ModalBody>
            <ModalFooter>
                <Button
                color="primary"
                onClick={() => props.this.Buscar()}
                >
                Buscar
                </Button>
                <Button
                className="btn btn-danger"
                onClick={() => props.this.cerrarModalBuscar()}
                >
                Cancelar
                </Button>
            </ModalFooter>
            </Modal>
    );
}


export default guest;
