import React, { Component} from "react";
import Swal from 'sweetalert2'
import Dropdown from 'react-dropdown';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import '../styles/asys.css'
import {
    Col,
    Row,
    Button,
    FormGroup,
    Container,
    Modal,
    Table
} from "reactstrap";

class AdminSystem extends Component{
    constructor() {
        super();
        this.state = {
            dep: '',
            deps : [], 
            load2: false,
            value: '',
            View1: false,
            View2: false,
            View3: false,
            View4: false,
            View5: false,
            View6: false,
            tasks1: [],
            tasks2: [],
            tasks3: [],
            tasks4: [],
            tasks5: [],
            tasks6: []
        };
        this.handleChange = this.handleChange.bind(this);
        this.cargarXml = this.cargarXml.bind(this);
        this.sendXml = this.sendXml.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
          [name]: value
        });
    }
    handleChangeP(e){
        this.setState({
            dep : e.value
        });
    }
    sendXml(){
        this.setState({load2: true})
        fetch('/carga', {
            method: 'POST',
            body: JSON.stringify({"xml": this.state.value}),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => { 
                Swal.fire({
                    title: data.msg,
                    showClass: {
                      popup: 'animate__animated animate__fadeInDown'
                    },
                    hideClass: {
                      popup: 'animate__animated animate__fadeOutUp'
                    }
                  })
                this.setState({load2: false})
            })
            .catch(err => console.error(err));
            this.setState({value: ''});

    }

    cerrarSesion=()=>{
        window.location.href='./';
    }

    cargarXml(e){
        const file = e.target.files[0];
        if ( !file ) return;

        const fileReader = new FileReader();

        fileReader.readAsText( file );

        fileReader.onload = () => {
        console.log( fileReader.result );
        this.setState({value: fileReader.result});
        }

        fileReader.onerror = () => {
        console.log( fileReader.error );
        }
    }

    render (){
        return <div>
            <nav role="navigation">
            <div id="menuToggle">
                <input type="checkbox"/>
                <span></span>
                <span></span>
                <span></span>
                <ul id="menu">
                <button onClick={()=>this.cerrarSesion()}><li>Cerrar Sesion</li></button>
                </ul>
            </div>
            </nav>
            <div className="xml">
                <h1>Admin Sistema</h1>
            </div>
            <div className="box"></div>
            <Container>
                <Tabs >
                    <TabList>
                        <Tab>Carga</Tab>
                        <Tab>Reporte 1</Tab>
                        <Tab>Reporte 2</Tab>
                        <Tab>Reporte 3</Tab>
                        <Tab>Reporte 4</Tab>
                        <Tab>Reporte 5</Tab>
                        <Tab>Reporte 6</Tab>
                    </TabList>
                    <TabPanel >
                        <Tab1 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_1 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_2 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_3 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_4 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_5 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_6 this = {this} />
                    </TabPanel>
                </Tabs>
            </Container>


            <Load this= {this}/>
        </div>
    }
}


function Tab1(props){
    return(
        <div className="xml">
                <h3>Carga Masiva</h3>
                <div>
                    <input type="file" 
                        multiple={false}
                        accept=".xml"
                        onChange={props.this.cargarXml}
                    />
                    <p>
                    <textarea name="value" value={props.this.state.value} onChange={props.this.handleChange} id="textExample" rows="10"  cols="60"></textarea>
                    </p>
                    <p>
                        <button onClick={props.this.sendXml} id='type'>Confirmar</button>
                    </p>
                </div>
        </div>
    );
}

function Report_1(props) {
    return(
        <Container>
        <div className="xmt">
            <h2>Reporte 1 </h2>
            </div>
            <div className="xm" >
            <text style={{textAlign: 'center'}}>Organigrama de la institución, se puede seleccionar tanto la institución completa o un departamento en específico</text>
        </div>      
        <div className="boxer"></div>      
        <FormGroup>
                <Dropdown name = "Dep" 
                options={props.this.state.deps} 
                onChange={props.this.handleChangeP} 
                value={props.this.state.dep}
                placeholder="Seleccione Departamento" />
        </FormGroup>
        <Button
            color="primary"
            onClick={() => props.this.enviar()}
            >
            Generar Reporte 
        </Button>
        <div className="box"></div>
        {props.this.state.View1 && 
         <Table>
            <thead>
            <tr>
                <th>Requisito</th>
                <th>Estado</th>
                <th>Ver</th>
            </tr>
            </thead>
            {props.this.state.tasks1.map((dato) => (
                <tbody>
                <tr>
                <td>{dato.nombre}</td>
                <td>{dato.puesto}</td> 
                </tr>
                </tbody>
            ))}
        </Table>}
        </Container>
    );
}

function Report_2(props) {
    return(
        <Container>
        <div className="xmt">
            <h2>Reporte 2</h2>
            </div>
            <div className="xm" >
            <text style={{textAlign: 'center'}}> Planilla de personas contratadas por un departamento en específico o todos los departamentos.</text>
        </div>      
        <div className="boxer"></div>      
        <FormGroup>
                <Dropdown name = "Dep" 
                options={props.this.state.deps} 
                onChange={props.this.handleChangeP} 
                value={props.this.state.dep}
                placeholder="Seleccione Departamento" />
        </FormGroup>
        <Button
            color="primary"
            onClick={() => props.this.enviar()}
            >
            Generar Reporte 
        </Button>
        <div className="box"></div>
        {props.this.state.View1 && 
         <Table>
            <thead>
            <tr>
                <th>Requisito</th>
                <th>Estado</th>
                <th>Ver</th>
            </tr>
            </thead>
            {props.this.state.tasks1.map((dato) => (
                <tbody>
                <tr>
                <td>{dato.nombre}</td>
                <td>{dato.puesto}</td> 
                </tr>
                </tbody>
            ))}
        </Table>}
        </Container>
    );
}


function Report_3(props) {
    return(
        <Container>
        <div className="xmt">
            <h2>Reporte 3</h2>
            </div>
            <div className="xm" >
            <text style={{textAlign: 'center'}}>Top 5 de departamentos con más personas contratadas</text>
        </div>      
        <div className="boxer"></div>      

        <Button
            color="primary"
            onClick={() => props.this.enviar()}
            >
            Generar Reporte 
        </Button>
        <div className="box"></div>
        {props.this.state.View1 && 
         <Table>
            <thead>
            <tr>
                <th>Requisito</th>
                <th>Estado</th>
                <th>Ver</th>
            </tr>
            </thead>
            {props.this.state.tasks1.map((dato) => (
                <tbody>
                <tr>
                <td>{dato.nombre}</td>
                <td>{dato.puesto}</td> 
                </tr>
                </tbody>
            ))}
        </Table>}
        </Container>
    );
}

function Report_4(props) {
    return(
        <Container>
        <div className="xmt">
            <h2>Reporte 4</h2>
            </div>
            <div className="xm" >
            <text style={{textAlign: 'center'}}>Top 5 de reclutadores con más invitaciones enviadas</text>
        </div>      
        <div className="boxer"></div>      
        <Button
            color="primary"
            onClick={() => props.this.enviar()}
            >
            Generar Reporte 
        </Button>
        <div className="box"></div>
        {props.this.state.View1 && 
         <Table>
            <thead>
            <tr>
                <th>Requisito</th>
                <th>Estado</th>
                <th>Ver</th>
            </tr>
            </thead>
            {props.this.state.tasks1.map((dato) => (
                <tbody>
                <tr>
                <td>{dato.nombre}</td>
                <td>{dato.puesto}</td> 
                </tr>
                </tbody>
            ))}
        </Table>}
        </Container>
    );
}

function Report_5(props) {
    return(
        <Container>
        <div className="xmt">
            <h2>Reporte 5 </h2>
            </div>
            <div className="xm" >
            <text style={{textAlign: 'center'}}>Top 5 de aplicantes con la mayor cantidad de documentos rechazados</text>
        </div>      
        <div className="boxer"></div>      
        <FormGroup>
                <Dropdown name = "Dep" 
