import React, { Component } from "react";
import Cookies from 'universal-cookie';
import Dropdown from 'react-dropdown';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import {getCurrentDate} from '../utils/date'

import {
    Table,
    Button,
    Container,
    Modal,
    ModalHeader,
    ModalBody,
    FormGroup,
    ModalFooter
} from "reactstrap";

const cookies = new Cookies();

class Reviewer extends Component{
    constructor(){
        super();
        if(cookies.get('id') === undefined) window.location.href = "./login"
        this.state = {
            id: cookies.get('id'),
            load: true,
            laod2: false, 
            nombre: '',
            modalBuscar:false,
            copy:[],
            modalVer: false,
            search:{
                nombre: null, 
                puesto: null, 
                estado: null
            },
            data: {
            },
            archivos: [],
            bus:false,
            expedientes:[],
            puestos:[],
            estados:["Sin revisar", "Aceptado", "Correccion"],
            motivo:'',
            modalMotivo:false,
            date : getCurrentDate()
        };
        this.handleChangeS = this.handleChangeS.bind(this);
        this.handleChangeP = this.handleChangeP.bind(this);
        this.handleChangeE = this.handleChangeE.bind(this);
        this.fetchProfile();
        this.fetchDepa();
        this.fetchTasks();
    }
    fetchDepa(){
        fetch('/depasr', {
            method: 'POST',
            body: JSON.stringify({
                per: this.state.id
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                this.fetchPuestos(data.id)
            })
            .catch(err => console.error(err));
    }

    fetchProfile(){
        fetch('/pers', {
            method: 'POST',
            body: JSON.stringify({
                per: this.state.id
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                this.setState({nombre: data.nombre})
            })
            .catch(err => console.error(err));
    }

    fetchPuestos(id){
        console.log(this.state.depa);
        fetch('/puestos', {
            method: 'POST',
            body: JSON.stringify({
                depa : id
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                this.setState({puestos: data});
            })
            .catch(err => console.error(err));
    }

    fetchTasks(){
        fetch('/selectre', {
            method: 'POST',
            body: JSON.stringify({
                personal: this.state.id,
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({expedientes: data, copy:data});
                this.setState({load: false})
            })
            .catch(err => console.error(err));
    }

    cerrarSesion(){
        cookies.remove('id', {path: "/"});
        window.location.href='./';
    }

    mostrarModalBuscar(){
        this.setState({modalBuscar:true})
    }

    handleChangeS(e){
        const { name, value } = e.target;
        this.setState({
          search:{
              ...this.state.search,
              [name]: value
          },
        });
    }
    handleChangeP(e){
        this.setState({
          search:{
              ...this.state.search,
              puesto: e.value
          },
        });
    }
    handleChangeE(e){
        this.setState({
          search:{
              ...this.state.search,
              estado: e.value
          },
        });
    }
    handleChange(e){
        this.setState({
          motivo: e.target.value
        });
    }
    buscar(){
        this.setState({load2: true})
        console.log(this.state.search)
        var estado = null
        if(this.state.search.estado === 'Sin Revisar') estado = '2';
        if(this.state.search.estado === 'Aceptado') estado = '1';
        if(this.state.search.estado === 'Correccion') estado = '0';

        fetch('/searche', {
            method: 'POST',
            body: JSON.stringify({
                personal: this.state.id,
                pueston: this.state.search.puesto,
                estado: estado,
                nombree: this.state.search.nombre
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                this.setState({expedientes: data});
                this.setState({load2: false, bus: true})
            })
            .catch(err => console.error(err));
        this.setState({modalBuscar: false})
        this.setState({
            search:{
                ...this.state.search,
                nombre: null,
                puesto: null,
                fecha: null
            },
        });
    }
    cerrarModalBuscar(){
        this.setState({modalBuscar: false})
    }

    cerrarModalVer(){
        this.setState({modalVer: false})
    }

    decargarExp(cv){
        this.setState({load2: true})
        fetch('/controller2', {
            method: 'POST',
            body: JSON.stringify({
                d:cv
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.blob())
            .then((blob) => {
                // Create blob link to download
                const url = window.URL.createObjectURL(
                  new Blob([blob]),
                );
                const link = document.createElement('a');
                link.href = url;
                link.setAttribute(
                  'download',
                  `${cv}`,
                );
                // Append to html link element page
                document.body.appendChild(link);
                // Start download
                link.click();
                this.setState({load2: false})
                // Clean up and remove the link
                link.parentNode.removeChild(link);
            });
    }
    aceptar(){
        this.setState({load2: true})
        fetch('/archivos', {
            method: 'PUT',
            body: JSON.stringify({
                ida: this.state.data.archivoID,
                estado: '1'
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({load2: false})
            })
            .catch(err => console.error(err));
    }
    rechazar(){
        this.setState({load2: true})
        fetch('/archivos', {
            method: 'PUT',
            body: JSON.stringify({
