import React, { Component} from "react";
import Swal from 'sweetalert2';
import Dropdown from 'react-dropdown';
import Pdf from "react-to-pdf";
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import '../styles/asys.css'
import {
    Button,
    FormGroup,
    Container,
    Modal,
    Table
} from "reactstrap";


const ref2 = React.createRef();
const ref3 = React.createRef();
const ref4 = React.createRef();
const ref5 = React.createRef();
const ref6 = React.createRef();

class AdminSystem extends Component{
    constructor() {
        super();
        this.state = {
            dep: '',
            deps : [], 
            load2: false,
            value: '',
            View1: false,
            View2: false,
            View3: false,
            View4: false,
            View5: false,
            View6: false,
            tasks1: [],
            tasks2: [],
            tasks3: [],
            tasks4: [],
            tasks5: [],
            tasks6: [],
            options: {
                orientation: 'landscape',
                unit: 'in',
            }
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleChangeP = this.handleChangeP.bind(this);
        this.cargarXml = this.cargarXml.bind(this);
        this.sendXml = this.sendXml.bind(this);
        this.fetchdepa();
    }
    fetchdepa() {
        fetch('/depas')
          .then(res => res.json())
          .then(data => {
            const aux = [];
            for(const i of data){
                aux.push(i.nombre)    
            }
            aux.push("INSTITUCION COMPLETA")
            this.setState({deps: aux});
        });
    }

    fetchtask2(){
        this.setState({load2: true})
        fetch('/report2', {
            method: 'POST',
            body: JSON.stringify(
                {depa: this.state.dep}
            ),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => { 
                Swal.fire({
                    title: "Reporte Generado Exitosamente",
                  })
                console.log(data)
                this.setState({View2: true, load2: false, tasks2: data.resultado})
            })
            .catch(err => console.error(err));
    }
    fetchTask3() {
        this.setState({load2: true})
        fetch('/report3')
          .then(res => res.json())
          .then(data => {
            Swal.fire({
                title: "Reporte Generado Exitosamente",
              })
            this.setState({View3: true, load2: false, tasks3: data.resultado})
        })
        .catch(err => console.error(err));
    }
    fetchTask4() {
        this.setState({load2: true})
        fetch('/report4')
          .then(res => res.json())
          .then(data => {
            Swal.fire({
                title: "Reporte Generado Exitosamente",
              })
            this.setState({View4: true, load2: false, tasks4: data.resultado})
        })
        .catch(err => console.error(err));
    }
    fetchTask5() {
        this.setState({load2: true})
        fetch('/report5')
          .then(res => res.json())
          .then(data => {
            Swal.fire({
                title: "Reporte Generado Exitosamente",
              })
            this.setState({View5: true, load2: false, tasks5: data.resultado})
        })
