          .then(res => res.json())
          .then(data => {
            Swal.fire({
                title: "Reporte Generado Exitosamente",
              })
            this.setState({View6: true, load2: false, tasks6: data.resultado})
        })
        .catch(err => console.error(err));
    }
    enviar(){

    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
          [name]: value
        });
    }
    handleChangeP(e){
        this.setState({
            dep : e.value
        });
    }
    sendXml(){
        this.setState({load2: true})
        fetch('/carga', {
            method: 'POST',
            body: JSON.stringify({"xml": this.state.value}),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => { 
                Swal.fire({
                    title: data.msg,
                    showClass: {
                      popup: 'animate__animated animate__fadeInDown'
                    },
                    hideClass: {
                      popup: 'animate__animated animate__fadeOutUp'
                    }
                  })
                this.setState({load2: false})
            })
            .catch(err => console.error(err));
            this.setState({value: ''});

    }

    cerrarSesion=()=>{
        window.location.href='./';
    }

    cargarXml(e){
        const file = e.target.files[0];
        if ( !file ) return;

        const fileReader = new FileReader();

        fileReader.readAsText( file );

        fileReader.onload = () => {
        console.log( fileReader.result );
        this.setState({value: fileReader.result});
        }

        fileReader.onerror = () => {
        console.log( fileReader.error );
        }
    }

    render (){
        return <div>
            <nav role="navigation">
            <div id="menuToggle">
                <input type="checkbox"/>
                <span></span>
                <span></span>
                <span></span>
                <ul id="menu">
                <button onClick={()=>this.cerrarSesion()}><li>Cerrar Sesion</li></button>
                </ul>
            </div>
            </nav>
            <div className="xml">
                <h1>Admin Sistema</h1>
            </div>
            <div className="box"></div>
            <Container>
                <Tabs >
                    <TabList>
                        <Tab>Carga</Tab>
                        <Tab>Reporte 1</Tab>
                        <Tab>Reporte 2</Tab>
                        <Tab>Reporte 3</Tab>
                        <Tab>Reporte 4</Tab>
                        <Tab>Reporte 5</Tab>
                        <Tab>Reporte 6</Tab>
                    </TabList>
                    <TabPanel >
                        <Tab1 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_1 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_2 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_3 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_4 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_5 this = {this} />
                    </TabPanel>
                    <TabPanel >
                        <Report_6 this = {this} />
                    </TabPanel>
                </Tabs>
            </Container>


            <Load this= {this}/>
        </div>
    }
}


function Tab1(props){
    return(
        <div className="xml">
                <h3>Carga Masiva</h3>
                <div>
                    <input type="file" 
                        multiple={false}
                        accept=".xml"
                        onChange={props.this.cargarXml}
                    />
                    <p>
                    <textarea name="value" value={props.this.state.value} onChange={props.this.handleChange} id="textExample" rows="10"  cols="60"></textarea>
                    </p>
                    <p>
                        <button onClick={props.this.sendXml} id='type'>Confirmar</button>
                    </p>
                </div>
        </div>
    );
}

function Report_1(props) {
    return(
        <Container>
        <div className="xmt">
            <h2>Reporte 1 </h2>
            </div>
            <div className="xm" >
            <text style={{textAlign: 'center'}}>Organigrama de la institución, se puede seleccionar tanto la institución completa o un departamento en específico</text>
        </div>      
        <div className="boxer"></div>      
        <FormGroup>
                <Dropdown name = "Dep" 
                options={props.this.state.deps} 
                onChange={props.this.handleChangeP} 
                value={props.this.state.dep}
                placeholder="Seleccione Departamento" />
        </FormGroup>
        <Button
            color="primary"
            onClick={() => props.this.fetchtask1()}
            >
            Generar Reporte 
        </Button>
        <div className="box"></div>
        {props.this.state.View5 && <div>
        <Button
        color="success"
        style={{float: 'right'}}
        onClick={() => props.this.enviar()}
        >
        Descargar Reporte 
        </Button>
         <Table>
            <thead>
            <tr>
                <th>Requisito</th>
                <th>Estado</th>
                <th>Ver</th>
            </tr>
            </thead>
            {props.this.state.tasks1.map((dato) => (
                <tbody>
                <tr>
                <td>{dato.nombre}</td>
                <td>{dato.puesto}</td> 
                </tr>
                </tbody>
            ))}
        </Table>
        </div>}
        </Container>
    );
}

