import React, { Component, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Dropdown from 'react-dropdown';
import { getCurrentDate } from '../utils/date'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import 'react-dropdown/style.css';
import Swal from 'sweetalert2'
import {
    Table,
    Button,
    Container,
    Modal,
    ModalHeader,
    ModalBody,
    FormGroup,
    ModalFooter
} from "reactstrap";



class AdminUser extends Component {

    constructor() {
        super();
        this.state = {
            modalActualizar: false,
            modalInsertar: false,
            load2: false,
            tasks: [],
            copy: [],
            data: {
                id: '',
                user: '',
                pass: '',
                estado: '',
                inicio: '',
                fin: '',
                correo: '',
                rol: '',
                depa: ''
            },
            search: {
                user: null,
                estado: null,
                inicio: null,
                fin: null,
                rol: null
            },
            estado: ["Activo", "Inactivo"],
            load: true,
            bus: false,
            modalBuscar: false,
            rols: ["Coordinador", "Reclutador", "Revisor"],
            depas: [],
            date: getCurrentDate()
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleChangeR = this.handleChangeR.bind(this);
        this.handleChangeD = this.handleChangeD.bind(this);
        this.handleChangeS = this.handleChangeS.bind(this);
        this.handleChangeES = this.handleChangeES.bind(this);
        this.handleChangeRS = this.handleChangeRS.bind(this);

        this.fetchTasks();
        this.fetchdepa();
    }

    fetchTasks() {
        fetch('/selectU')
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({ tasks: data, load: false, copy: data });
            });
    }
    fetchdepa() {
        fetch('/depas')
            .then(res => res.json())
            .then(data => {
                const aux = [];
                for (const i of data) {
                    aux.push(i.nombre)
                }
                this.setState({ depas: aux });
            });
    }
    handleChangeES(e) {
        this.setState({
            search: {
                ...this.state.search,
                estado: e.value
            },
        });
    }
    handleChangeRS(e) {
        this.setState({
            search: {
                ...this.state.search,
                rol: e.value
            },
        });
    }


    handleChangeS(e) {
        const { name, value } = e.target;
        this.setState({
            search: {
                ...this.state.search,
                [name]: value
            },
        });
    }

    buscar(Finicio, Ffinal) {
        this.setState({ load2: true })
        console.log(this.state.search)
        let inicio = null, final = null;
        if (Finicio != null) inicio = `${Finicio.getFullYear()}-${Finicio.getMonth() + 1}-${Finicio.getDate()}`
        if (Ffinal != null) final = `${Ffinal.getFullYear()}-${Ffinal.getMonth() + 1}-${Ffinal.getDate()}`
        console.log(inicio)
        console.log(final)
        this.setState({ load: true })

        let estado = null;
        if (this.state.search.estado === 'Activo') {
            estado = '1'
        } else if (this.state.search.estado === 'Inactivo') {
            estado = '0'
        }
        fetch('/searchU', {
            method: 'POST',
            body: JSON.stringify({
                usuario: this.state.search.user,
                estado: estado,
                inicio: inicio,
                final: final,
                rol: this.state.search.rol
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(data => {
                this.setState({ tasks: data });
                this.setState({ load2: false })
            })
            .catch(err => console.error(err));

        this.setState({ bus: true, modalBuscar: false })
        this.setState({
            search: {
                ...this.state.search,
                user: null,
                estado: null,
                inicio: null,
                fin: null,
                rol: null
            },
        });
    }

    mostrarModalBuscar() {
        this.setState({ modalBuscar: true })
    }
    cerrarModalBuscar() {
        this.setState({ modalBuscar: false })
        //this.fetchTasks();
    }
    cerrarBusqueda() {
        this.setState({ tasks: this.state.copy, bus: false })
    }


    mostrarModalInsertar() {
        this.setState({ modalInsertar: true })
    }
    cerrarModalInsertar() {
        this.setState({ modalInsertar: false })
        this.fetchTasks();
    }
    mostrarModalActualizar(dato) {
        this.setState({
            data: dato,
            modalActualizar: true
        })
    }
    cerrarModalActualizar() {
        this.setState({ modalActualizar: false })
    }

    editar(dato) {
        this.setState({ load2: true })
        console.log(this.state.data)
        fetch('/updateU', {
            method: 'PUT',
            body: JSON.stringify({
                idP: dato.id,
                usuario: dato.user,
                pass: dato.pass,
                rol: dato.rol,
                correo: dato.correo,
                depa: dato.depa
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(data => {
                Swal.fire(
                    'Hecho!',
                    data.msg,
                    'info'
                )
                this.fetchTasks();
                this.setState({ load2: false })
            })
            .catch(err => console.error(err));
        this.cerrarModalActualizar();
        this.fetchTasks();
    }
    eliminar(dato) {
        this.setState({ load2: true })
        console.log(this.state.data)
        fetch('/deleteU', {
            method: 'PUT',
            body: JSON.stringify({
                idP: dato.id,
                fin: this.state.date
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(data => {
                Swal.fire(
                    'Hecho!',
                    data.msg,
                    'info'
                )
                this.fetchTasks();
                this.setState({ load2: false })
            })
            .catch(err => console.error(err));
        this.fetchTasks();
    }
    insertar() {
        this.setState({ load2: true })
        console.log(this.state.data)
        fetch('/insertU', {
            method: 'POST',
            body: JSON.stringify({
                usuario: this.state.data.user,
                pass: this.state.data.pass,
                inicio: this.state.date,
                rol: this.state.data.rol,
                correo: this.state.data.correo,
                depa: this.state.data.depa
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(data => {
                Swal.fire(
                    'Hecho!',
                    data.msg,
                    'info'
                )
                this.fetchTasks();
                this.setState({ load2: false })
            })
            .catch(err => console.error(err));
        this.cerrarModalInsertar()
        this.fetchTasks();
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
            data: {
                ...this.state.data,
                [name]: value
            },
        });
    }

    handleChangeR(e) {
        this.setState({
            data: {
                ...this.state.data,
                rol: e.value
            },
        });
    }
    handleChangeD(e) {
        this.setState({
            data: {
                ...this.state.data,
                depa: e.value
            },
        });
    }

    cerrarSesion = () => {
        window.location.href = './';
    }

    render() {
        return (
            <div className="crud">
                <Load this={this} />
                <nav role="navigation">
                    <div id="menuToggle">
                        <input type="checkbox" />
                        <span></span>
                        <span></span>
                        <span></span>
                        <ul id="menu">
                            <button onClick={() => this.mostrarModalBuscar()}><li>Buscar Usuario</li></button>
                            <div className='boxer' />
                            <button onClick={() => this.cerrarSesion()}><li>Cerrar Sesion</li></button>
                        </ul>
                    </div>
                </nav>
                <div className='xml'>
                    <h1>Usuarios</h1>
                </div>
                <Container>
                    <br />
                    <Button color="primary" onClick={() => this.mostrarModalInsertar()}>Crear Usuario</Button>
                    <br />
                    <br />
                    {(() => {
                        if (this.state.bus === true) {
                            return <Container>
                                <Button style={{ float: 'right' }}
                                    className="btn btn-danger"
                                    onClick={() => this.cerrarBusqueda()}
                                >
                                    X
                                </Button>
                                <div class="box"></div>
                            </Container>

                        }
                    })()}
                    <Table>
                        <thead>
                            <tr>
                                <th>Usuario</th>
                                <th>Contraseña</th>
                                <th>Correo</th>
                                <th>Fecha Inicio</th>
                                <th>Fecha Fin</th>
                                <th>Rol</th>
                                <th>Departamento</th>
                            </tr>
                        </thead>

                        {this.state.tasks.map((dato) => (
                            (() => {
                                if (dato.estado === '1') {
                                    return <Ifyes dato={dato} this={this} />
                                } else {
                                    return <Elsen dato={dato} this={this} />
                                }
                            })()
                        ))}

                    </Table>
                </Container>

                <Modal isOpen={this.state.modalInsertar} fade={false}>
                    <ModalHeader>
                        <div><h3>Insertar Usuario</h3></div>
                    </ModalHeader>
                    <ModalBody>
                        <FormGroup>
                            <label>
                                Usuario:
                            </label>
                            <input
                                className="form-control"
                                name="user"
                                type="text"
                                onChange={this.handleChange}
                            />
                        </FormGroup>
                        <FormGroup>
