import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import {getCurrentDate} from '../utils/date'

import {
    Table,
    Button,
    Container,
    Modal,
    ModalHeader,
    ModalBody,
    FormGroup,
    ModalFooter
} from "reactstrap";

class AdminUser extends Component{
    constructor() {
        super();
        this.state = {
            modalActualizar: false,
            modalInsertar: false,
            tasks: [],
            data :{
                id: '',
                user: '',
                pass: '',
                estado: '',
                inicio: '',
                fin: '',
                rol: '',
                depa: ''
            },
            rols: ["Coordinador", "Reclutador", "Revisor"], 
            depas: [],
            date : getCurrentDate()
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleChangeR = this.handleChangeR.bind(this);
        this.handleChangeD = this.handleChangeD.bind(this);
        this.fetchTasks();
        this.fetchdepa();
    }
    fetchTasks() {
        fetch('/selectU')
          .then(res => res.json())
          .then(data => {
            console.log(data)
            this.setState({tasks: data});
          });
    }
    fetchdepa() {
        fetch('/depas')
          .then(res => res.json())
          .then(data => {
            const aux = [];
            for(const i of data){
                aux.push(i.nombre)    
            }
            this.setState({depas: aux});
        });
    }


    mostrarModalInsertar(){
        this.setState({modalInsertar: true})
    }
    cerrarModalInsertar(){
        this.setState({modalInsertar: false})
        this.fetchTasks();
    }
    mostrarModalActualizar(dato){

    }
    cerrarModalActualizar(){

    }

    editar(dato){

    }
    eliminar(dato){

    }
    insertar(){
        console.log(this.state.data)
        fetch('/insertU', {
            method: 'POST',
            body: JSON.stringify({
                usuario: this.state.data.user,
                pass: this.state.data.pass,
                inicio: this.state.date,
                rol: this.state.data.rol,
                depa:this.state.data.depa
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                window.alert(data.msg);
                this.fetchTasks();
            })
            .catch(err => console.error(err));
        this.cerrarModalInsertar()
        this.fetchTasks();
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
          data:{
              ...this.state.data,
              [name]: value
          },
        });
    }

    handleChangeR(e) {
        this.setState({
          data:{
              ...this.state.data,
              rol: e.value
          },
        });
    }
    handleChangeD(e) {
        this.setState({
          data:{
              ...this.state.data,
              depa: e.value
          },
        });
    }

    render (){
        return(
            <div className="crud">
            <Container>
            <br />
            <Button color="success" onClick={()=>this.mostrarModalInsertar()}>Crear Usuario</Button>
            <br />
            <br />
            <Table>
                <thead>
                <tr>
                    <th>Usuario</th>
                    <th>Contraseña</th>
                    <th>Fecha Inicio</th>
                    <th>Fecha Fin</th>
                    <th>Rol</th>
                    <th>Departamento</th>
                </tr>
                </thead>

                {this.state.tasks.map((dato) => (
                    (() => {
                        if(dato.estado == 1){
                            return <Ifyes dato = {dato}/>
                        }else{
                            return <Elsen dato = {dato}/>
                        }
                    })()
                ))}

