        fetch('/selectPr', {
            method: 'POST',
            body: JSON.stringify({
                cui: this.state.id
            }),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
            })
            .then(res => res.json())
            .then(data => {
                this.setState({profile: data});
                this.Comprobar(data.primera)
            })
            .catch(err => console.error(err));
    }


    Comprobar(primera){
        if(primera == '1'){
            this.setState({modalCargar: true});
        }
    }

    fetchRequisitos(){
        fetch('/requisitos', {
            method: 'POST',
            body: JSON.stringify({
